import React, { useState, useEffect } from "react";
import {
	Container,
	Col,
	Row,
	Button,
	Card,
	CardBody,
	CardHeader,
} from "reactstrap";
import { Tabs, Tab } from "react-bootstrap";
import Header from "../components/Header";
import API from "../utills/Api";
const Auth = () => {
	const [email, setemail] = useState("");
	const [password, setpassword] = useState("");
	const [showLogin, setshowLogin] = useState(false);
	const [showSignup, setshowSignup] = useState(false);
	const [Username, setUsername] = useState("");

	useEffect(() => {
		async function checkLogin() {
			let response = await API.get("/check", {
				headers: {
					"x-access-tokens": localStorage.getItem("jwt"),
				},
			});
			response = await response;
			let res_data = { ...response.data };
			console.log(res_data);
			if (res_data.isAuthenticated === false) {
				setshowLogin(true);
			} else setshowLogin(false);
		}
		checkLogin();
	}, []);

	const loginApi = async () => {
		const data = { email: email, password: password };
		await API.post("/login", { ...data })
			.then((res) => {
				console.log(res.data.token);
				localStorage.setItem("jwt", res.data.token);
				setshowLogin(false);
				setshowSignup(false);
			})
			.catch((err) => {
				console.log(err);
			});
	};

	const signupApi = async () => {
		const data = { email: email, password: password, username: Username };
		await API.post("/signup", { ...data })
			.then((res) => {
				console.log(res);
			})
			.catch((err) => {
				console.log(err);
			});
	};

	const loginJsx = () => {
		return (
			<CardBody>
				<Container>
					<Row>
						<Col xs={5} style={{ margin: "auto" }}>
							<form
								onSubmit={(e) => {
									e.preventDefault();
									loginApi();
								}}>
								<div class='form-group'>
									<label for='exampleInputEmail1'>Email</label>
									<input
										class='form-control'
										placeholder='Email'
										type='email'
										value={email}
										onChange={(e) => setemail(e.target.value)}
									/>
								</div>
								<div class='form-group'>
									<label for='exampleInputEmail1'>Password</label>
									<input
										class='form-control'
										placeholder='Password'
										type='password'
										value={password}
										onChange={(e) => setpassword(e.target.value)}
									/>
								</div>
								<button
									style={{ margin: "20px", marginTop: "24px" }}
									type='submit'
									class='btn btn-primary'>
									Login
								</button>
								<button
									style={{ margin: "20px", marginTop: "24px" }}
									onClick={(e) => {
										e.preventDefault();
										setshowSignup(true);
										setshowLogin(false);
									}}
									class='btn btn-primary'>
									SignUp
								</button>
							</form>
						</Col>
					</Row>
				</Container>
			</CardBody>
		);
	};

	const signupJsx = () => {
		return (
			<CardBody>
				<Container>
					<Row>
						<Col xs={5} style={{ margin: "auto" }}>
							<form
								onSubmit={(e) => {
									e.preventDefault();
									signupApi();
								}}>
								<div class='form-group'>
									<label for='exampleInputEmail1'>Username</label>
									<input
										class='form-control'
										placeholder='Username'
										type='text'
										value={Username}
										onChange={(e) => setUsername(e.target.value)}
									/>
								</div>
								<div class='form-group'>
									<label for='exampleInputEmail1'>Email</label>
									<input
										class='form-control'
										placeholder='Email'
										type='email'
										value={email}
										onChange={(e) => setemail(e.target.value)}
									/>
								</div>
								<div class='form-group'>
									<label for='exampleInputEmail1'>Password</label>
									<input
										class='form-control'
										placeholder='Password'
										type='password'
										value={password}
										onChange={(e) => setpassword(e.target.value)}
									/>
								</div>
								<button
									style={{ margin: "20px", marginTop: "24px" }}
									type='submit'
									class='btn btn-primary'>
									Sign Up
								</button>
								<button
									style={{ margin: "20px", marginTop: "24px" }}
									class='btn btn-primary'
									onClick={(e) => {
										e.preventDefault();
										setshowSignup(false);
										setshowLogin(true);
									}}>
									Login
								</button>
							</form>
						</Col>
					</Row>
				</Container>
			</CardBody>
		);
	};
	return (
		<>
			<Header
				title='A Tool For Future!'
				desc1='This is a simple hero unit, a simple Jumbotron-style component
								for calling extra attention to featured content or information.'
				desc2='It uses utility classes for typography and spacing to space
                content out within the larger container.'
			/>
			<Container fluid className='contant-container'>
				<Row>
					<Col>
						<Card className='train-cards-collapse'>
							<CardHeader style={{ textAlign: "center" }}>
								<span style={{ fontSize: "40px" }}>
									{showLogin ? "Authentication" : "Api Key"}
								</span>
							</CardHeader>
							{showLogin ? loginJsx() : null}
							{showSignup ? signupJsx() : null}
						</Card>
					</Col>
				</Row>
			</Container>
		</>
	);
};

export default Auth;
