import React, { useState } from "react";
import Header from "../components/Header";
import {
	Jumbotron,
	Container,
	Row,
	Col,
	Button,
	Card,
	CardTitle,
	CardText,
	FormGroup,
	Label,
	Input,
} from "reactstrap";
import { Form } from "react-bootstrap";
import API from "../utills/Api";
import ReactApexChart from "react-apexcharts";
const YieldFinder = () => {
	const [SelectedState, setSelectedState] = useState("");
	const [SelectedCity, setSelectedCity] = useState("");
	const [SelectedSeason, setSelectedSeason] = useState("");
	const [SelectedCrop, setSelectedCrop] = useState("");
	const [Area, setArea] = useState("");
	const [show, setShow] = useState(false);
	const [results, setResults] = useState({
		barGraphLabel: [
			"kharif",
			"rabi",
			"summer",
			"winter",
			"autumn",
			"whole year",
		],
		barGraphvalue: {
			autumn: 1.38,
			kharif: 1.65,
			rabi: 2.44,
			summer: 2.18,
			"whole year": 1.38,
			winter: 1.48,
		},
		pieChartLabel: [
			"kharif",
			"rabi",
			"summer",
			"winter",
			"autumn",
			"whole year",
		],
		pieChartValue: [16, 23, 21, 14.000000000000002, 13, 13],
		predProduction: 139.75819654637144,
		predYield: 1.1634881497366918,
	});
	const [YieldBarGraph, setYieldBarGraph] = useState();
	const [PieChart, setPieChart] = useState();

	const showResult = async () => {
		const data = {
			state: "gujarat",
			city: "amreli",
			season: "kharif",
			crop: "rice",
			area: 120.12,
		};
		await API.post("/yield", { ...data })
			.then((res) => {
				const res_data = { ...res.data };
				console.log(res_data);
				let yield_bar = {
					series: [
						{
							name: "Predicted Yield",
							data: res_data.barGraphvalue,
						},
					],
					options: {
						chart: {
							type: "bar",
							height: 0,
						},
						plotOptions: {
							bar: {
								borderRadius: 6,
								horizontal: true,
							},
						},
						dataLabels: {
							enabled: false,
						},
						xaxis: {
							categories: res_data.barGraphLabel,
						},
					},
				};
				let pie_chart = {
					series: res_data.pieChartValue,
					options: {
						chart: {
							width: "100%",
							type: "pie",
						},
						labels: res_data.pieChartLabel,
						theme: {
							monochrome: {
								enabled: true,
							},
						},
						plotOptions: {
							pie: {
								dataLabels: {
									offset: -5,
								},
							},
						},
						dataLabels: {
							formatter(val, opts) {
								const name = opts.w.globals.labels[opts.seriesIndex];
								return [name, val.toFixed(1) + "%"];
							},
						},
						legend: {
							show: true,
						},
					},
				};
				setResults(res_data);
				setPieChart(pie_chart);
				setYieldBarGraph(yield_bar);
				setShow(true);
			})
			.catch((err) => {
				console.log(err);
			});
	};
	return (
		<>
			<Header
				title='Find The Real Price!'
