										onClick={() => setToggequestion(3)}>
										<span style={{ fontSize: "40px" }}>
											Your Trained Models
										</span>
									</CardHeader>
									<Container>
										<Row>
											<Col>
												<CardBody>
													<Table striped bordered hover>
														<thead>
															<tr>
																<th>#</th>
																<th>Model Name</th>
																<th>Use Model</th>
															</tr>
														</thead>
														<tbody>
															{ModelNames.map((nmx, idx) => {
																console.log(ModelNames);
																console.log(nmx);
																return (
																	<tr>
																		<td>{idx + 1}</td>
																		<td>{firstletterCap(nmx)}</td>
																		<td>
																			<Button
																				onClick={(e) => {
																					e.preventDefault();
																					setUseModel(nmx);
																				}}
																				color='primary'>
																				Use {firstletterCap(nmx)} Model
																			</Button>
																		</td>
